import '../App.css';
import React from 'react';
import {Left} from "../App";
import axios from 'axios';

export let get_all_posts = (e)=> {
    const postsData = [];
    let url = '/';
    axios.get(url).then((res)=>{
        for (let post in res.data){
            let posts ={
                id: 'Blog_post_#'+post['post_id'],
                className: "inner_table",
                classname: "inner_text",
                post: post['post_id'],
                order: post['post_id'],
                number: post['post_id'],
                author: post['author_id'],
                text: post['title'] +"\n"+post['body']
            }
            postsData.push(posts);
        }
        return postsData;
    })
}
export let postsData = get_all_posts();
