import React, {useState} from 'react';
import {makeStyles} from '@material-ui/core/styles';
import Input from '@material-ui/core/Input';
import InputLabel from '@material-ui/core/InputLabel';
import InputAdornment from '@material-ui/core/InputAdornment';
import FormControl from '@material-ui/core/FormControl';
import TextField from '@material-ui/core/TextField';
import Grid from '@material-ui/core/Grid';
import AccountCircle from '@material-ui/icons/AccountCircle';
import {Button, IconButton} from "@material-ui/core";
import "../App.css";
import {Link} from "react-router-dom";
import VpnKeyIcon from '@material-ui/icons/VpnKey';
import axios from "axios";
const useStyles = makeStyles((theme) => ({
    margin: {
        margin: theme.spacing(3),
    },
    Button:{
        backgroundColor:"#8FBC8B",
        margin: theme.spacing(3),
        color:"#E0FFFF",
    },
}));

export default function InputWithIcon() {
    const classes = useStyles();
    const [user,setUser] = useState(null);
    const [pass,setPass] = useState(null);
    const [resp,setResp] = useState(null);
    let changeUser = (e)=>{
        setUser(e.target.value);
    }
    let changePass = (e)=>{
        setPass(e.target.value);
    }
    let doLogin = ()=>{
      let url = '/login';
      let data = {
          user: user,
          pass: pass,
      }
      axios.post(url,data).then((res)=>{
          setResp("Success: city added.")
      }).catch()

    }
    return (
        <div>
            <Grid
                container
                direction="column"
                justify="space-evenly"
                alignItems="center"
            >
                <h1 className={classes.margin}>Login</h1>
                <FormControl className={classes.margin}>
                    <InputLabel htmlFor="input-with-icon-adornment">User Name</InputLabel>
                    <Input
                        id="input-with-icon-adornment"
                        placeholder=" User Name"
                        label=" User Name"
                        onChange={changeUser}
                        startAdornment={
                            <InputAdornment position="start">
                                <AccountCircle/>
                            </InputAdornment>
                        }
                    />
                </FormControl>
                <TextField
                    className={classes.margin}
                    id="input-with-icon-textfield"
                    placeholder="Password"
                    label="Password"
                    type="password"
                    position="center"
                    onChange={changePass}
                    InputProps={{
                        startAdornment: (
                            <InputAdornment position="start">
                                <VpnKeyIcon/>
                            </InputAdornment>
                        ),
                    }}
                />
                <Button id="loginButton" className={classes.Button} variant="contained" onClick={doLogin}>
                    Login
                </Button>
                <Link to = "./login" className={classes.margin} >Forgot Username / password</Link>
            </Grid>
        </div>
    );
}
