import '../App.css';
import React ,{useState,useEffect} from 'react';
import {App, Left} from "../App";
import axios from 'axios';
import {Link} from "react-router-dom";
let create_obj_list = (data)=>{
    let tempPostsData = [];
    for (let post in data) {
        let posts = {
            id: 'Blog_post_#' + data[post].post_id,
            className: "inner_table",
            classname: "inner_text",
            post: data[post].post_id,
            order: data[post].upload_time,
            number: data[post].post_id,
            author: data[post].author,
            text: data[post].title + "\n" + data[post].body
        }
        tempPostsData.push(posts);
    }
    return tempPostsData;
}
const Post = props => {
    const {post} = props;
    return <p><Left id={post.id} className={post.className} classname={post.classname} post={post.post}
                    order={post.order}
                    number={post.number} author={post.author} text={post.text}/></p>
};


let Posts = (props)=> {
    const [Data, setData] = useState(props.data);
    let Get_all_posts =  ()=> {
        let url = '/posts';
        const res = axios.get(url);
        res.then((resalts) => {
            console.log(resalts.data);
            console.log(Data);
            setData(resalts.data);

        })
    }
    let post_list = create_obj_list(Data);
    const listItems = post_list.map((postItem) =>
        <Post post={postItem}/>
    );
    return (<div>

            <Link to ="/Post" className="left_title" id= "nan2"  onClick = {Get_all_posts}>Post</Link>

            {listItems}
            </div>);
}
export default Posts;
