import '../App.css';
import React from 'react';
import {Left} from "../App";
import axios from 'axios';

export const postsData = [];
let gt_all_posts = (e)=> {
    let url = '/';
    axios.get(url).then((res)=>{
        postsData = [];
        for (post in res.data){
            let posts ={
                id: 'Blog_post_#'+post[post_id],
                className: "inner_table",
                classname: "inner_text",
                post: post[post_id],
                order: post[post_id],
                number: post[post_id],
                author: post[author_id],
                text: post[title] +"\n"+post[body]
            }
            postsData.push(posts);
        }
    })
}


export const postsData1 = [{
    id: "Blog_post_#1",
    className: "inner_table",
    classname: "inner_text",
    post: "1",
    order: "first",
    number: "1",
    author: "israel",
    text: <span id="firstBlog">and how to write a new
                    post in my<br/>blog,
                    you find it <a href='#here'>here</a></span>
},
    {
        id: "Blog_post_#2",
        className: "inner_table",
        classname: "inner_text",
        post: "2",
        order: "second",
        number: "2",
        author: "Joe",
        text: ""
    },
    {
        id: "Blog_post_#3",
        className: "inner_table",
        classname: "inner_text",
        post: "3",
        order: "third",
        number: "3",
        author: "israel",
        text: ""
    }];
export const PostList = props => {
    const {posts} = props;
    const listItems = posts.map((postItem) =>
        <Post post={postItem}/>
    );
    return <div>{listItems}</div>
};
export const Post = props => {
    const {post} = props;
    return <p><Left id={post.id} className={post.className} classname={post.classname} post={post.post}
                    order={post.order}
                    number={post.number} author={post.author} text={post.text}/></p>
};
