import '../App.css';
import React ,{useState} from 'react';
import {Left} from "../App";
import axios from 'axios';

 export let Get_all_posts = async ()=>{
    let tempPostsData = [];
    let url = '/posts';
    const [data,setData] = useState([])
    const res = await axios.get(url);
    setData(res.data);
     for (let post in res.data) {
            alert(post);
            let posts = {
                id: 'Blog_post_#' + data[post].post_id,
                className: "inner_table",
                classname: "inner_text",
                post: data[post].post_id,
                order: data[post].upload_time,
                number: data[post].post_id,
                author: data[post].author,
                text: data[post].title + "\n" + data[post].body
            }
            tempPostsData.push(posts);
        }
     setData(tempPostsData);
     const PostList = () => {
         const {posts} = data;
         const listItems = posts.map((postItem) =>
             <Post post={postItem}/>
         );
         return <div>{listItems}</div>
     };
     const Post = props => {
         const {post} = props;
         return <p><Left id={post.id} className={post.className} classname={post.classname} post={post.post}
                         order={post.order}
                         number={post.number} author={post.author} text={post.text}/></p>
     };

     PostList();
 }

