import React, {useState} from "react";
import axios from "axios";
import {makeStyles} from "@material-ui/core/styles";
import Grid from "@material-ui/core/Grid";
import {Button} from "@material-ui/core";
import {Link} from "react-router-dom";
const useStyles = makeStyles((theme) => ({
    margin: {
        margin: theme.spacing(3),
    },
    Button:{
        backgroundColor:"#FF1493",
        margin: theme.spacing(3),
        color:"#E0FFFF",
    },
}));
export default function UserLogout () {
    let classes =useStyles();
    const [resp,setResp] = useState(null);
    let doLogout = ()=> {
        let url = '/api/logout';
        let data = {
            status: 'true',
        }
        axios.post(url, data).then((res) => {
            setResp("Success: user logout.");

        }).catch((err) => {
            setResp("Error: failed to logout.");
        })
    }
    return (
        <div>
            <Grid
                container
                direction="column"
                justify="space-evenly"
                alignItems="center"
            >
            <Button id='logoutButton' className={classes.Button} variant="contained" onClick={doLogout}>Logout</Button>
            <div>
                {resp?resp:null}
            </div>
             <Link to = "./login" className={classes.margin} >Forgot Username / password</Link>
            </Grid>
        </div>)
     }
