import {useState} from "react";

let NewPost =()=>{
    const [push,setPush] =useState(0);
    let Push = ()=>{
        setPush(1)
    }
    if (push == 0){
        return(
            <div id = "newPostRoot">
                <button onClick={Push}>create a new post</button>
            </div>
        )
    } else {
        return(<form id = "myForm">
            <label htmlFor= "title">Title</label>
            <textarea name="title" id="title" cols="30" rows="1"/>
            <label htmlFor="body">Body</label>
            <textarea name="body" id="body" cols="30" rows="100"/>
            <label htmlFor="category">Category</label>
            <textarea name="category" id="category" cols="30" rows="1"/>
            <button type="submit">Post</button>
        </form>
        )
    }

}
export default NewPost;
