import '../App.css';
import React ,{useState} from 'react';
import {Left} from "../App";
import axios from 'axios';
let create_obj_list = (data)=>{
    let tempPostsData = [];
    for (let post in data) {
        let posts = {
            id: 'Blog_post_#' + data[post].post_id,
            className: "inner_table",
            classname: "inner_text",
            post: data[post].post_id,
            order: data[post].upload_time,
            number: data[post].post_id,
            author: data[post].author,
            text: data[post].title + "\n" + data[post].body
        }
        tempPostsData.push(posts);
    }
    return tempPostsData;
}
const Post = props => {
    const {post} = props;
    return <p><Left id={post.id} className={post.className} classname={post.classname} post={post.post}
                    order={post.order}
                    number={post.number} author={post.author} text={post.text}/></p>
};
