import '../App.css';
import React from 'react';
import {Left} from "../App";
import axios from 'axios';
export let postsData ;
export let get_all_posts = ()=> {
    let tempPostsData = [];
    let url = '/posts';
    axios.get(url).then((res)=>{
        alert(res.data[0].post_id);
        for (let post in res.data){
            alert(res.data[post].author);
            let posts ={
                id: 'Blog_post_#'+res.data[post].post_id,
                className: "inner_table",
                classname: "inner_text",
                post: res.data[post].post_id,
                order: res.data[post].upload_time,
                number: res.data[post].post_id,
                author: res.data[post].author,
                text: res.data[post].title +"\n"+res.data[post].body
            }
            postsData.push(posts);
        }

    });

}
get_all_posts();
alert(postsData[0].id);
export const postsData1 = [{
    id: "Blog_post_#1",
    className: "inner_table",
    classname: "inner_text",
    post: "1",
    order: "first",
    number: "1",
    author: "israel",
    text: <span id="firstBlog">and how to write a new
                    post in my<br/>blog,
                    you find it <a href='#here'>here</a></span>
},
    {
        id: "Blog_post_#2",
        className: "inner_table",
        classname: "inner_text",
        post: "2",
        order: "second",
        number: "2",
        author: "Joe",
        text: ""
    },
    {
        id: "Blog_post_#3",
        className: "inner_table",
        classname: "inner_text",
        post: "3",
        order: "third",
        number: "3",
        author: "israel",
        text: ""
    }];
export const PostList = props => {
    const {posts} = props;
    const listItems = posts.map((postItem) =>
        <Post post={postItem}/>
    );
    return <div>{listItems}</div>
};
export const Post = props => {
    const {post} = props;
    return <p><Left id={post.id} className={post.className} classname={post.classname} post={post.post}
                    order={post.order}
                    number={post.number} author={post.author} text={post.text}/></p>
};
