import '../App.css';
import React ,{useState} from 'react';
import {Left} from "../App";
import axios from 'axios';
let create_obj_list = (data)=>{
    let tempPostsData = [];
    for (let post in data) {
        let posts = {
            id: 'Blog_post_#' + data[post].post_id,
            className: "inner_table",
            classname: "inner_text",
            post: data[post].post_id,
            order: data[post].upload_time,
            number: data[post].post_id,
            author: data[post].author,
            text: data[post].title + "\n" + data[post].body
        }
        tempPostsData.push(posts);
    }
    return tempPostsData;
}
const Post = props => {
    const {post} = props;
    return <p><Left id={post.id} className={post.className} classname={post.classname} post={post.post}
                    order={post.order}
                    number={post.number} author={post.author} text={post.text}/></p>
};


let Posts = ()=> {
    const [Data, setData] = useState(postsData1);
    let Get_all_posts =  (prop)=> {
        let url = '/posts';
        const res = axios.get(url);
        res.then(() => {
            console.log(res.data);
            console.log(Data);
            setData(res.data);

        })
    }
    debugger;
    Get_all_posts();
    let post_list = create_obj_list(Data);
    const listItems = post_list.map((postItem) =>
        <Post post={postItem}/>
    );
    return (<div>{listItems}</div>);
}
const postsData1 = [{
    id: "Blog_post_#1",
    className: "inner_table",
    classname: "inner_text",
    post: "1",
    order: "first",
    number: "1",
    author: "israel",
    text: <span id="firstBlog">and how to write a new
                    post in my<br/>blog,
                    you find it <a href='#here'>here</a></span>
}]
export default Posts;
