import '../App.css';
import React from 'react';
import {Left} from "../App";
import axios from 'axios';

let get_all_posts = ()=> {
    let tempPostsData = [];
    let url = '/posts';
    let data = axios.get(url)
    data.then((res)=>alert(res))
    for (let post in data.data) {
            alert(post.post_id);
            let posts = {
                id: 'Blog_post_#' + post.post_id,
                className: "inner_table",
                classname: "inner_text",
                post: post.post_id,
                order: post.upload_time,
                number: post.post_id,
                author: post.author,
                text: post.title + "\n" + post.body
            }
            tempPostsData.push(posts);
        }
    return tempPostsData;
    }


export let postsData = get_all_posts();
setTimeout(alert("hii"+postsData),5000000000);
